/* checkbox */
.checkbox-container {
  display: flex;
  align-items: center;
  position: relative;
  width: 90%;
  margin: 20px auto 0 auto;
}

.checkbox-container label {
  cursor: pointer;
  display: flex;
  color: #222;
  //font-size: 0.65rem;
  padding: 0.5em;
  margin-left: 25px;
  font-size: 12px;
  text-align: left;
}

.checkbox-container input[type="checkbox"] {
  cursor: pointer;
  opacity: 0;
  position: absolute;
}

.checkbox-container label::before {
  content: "";
  background-color: #fff;
  border: 1px solid #666;
  // border-radius: 5px;
  display: inline-block;
  position: absolute;
  left: 0px;
  top: 10px;
  width: 20px;
  height: 20px;
}

.label {
  margin-left: 20px;
}

.label:after {
  content: "\2713";
  font-size: 1.1rem;
  display: inline-block;
  position: absolute;
  top: 6px;
  left: 4px;
  transform: scale(0);
  transition: transform 0.2s linear;
}

.checkbox:checked + .label:after {
  transform: scale(1);
}
