/* FONTS */

@font-face {
  font-family: "Gotham Bold";
  src: url(../assets/fonts/Gotham-Bold.otf);
}

@font-face {
  font-family: "Gotham Book";
  src: url(../assets/fonts/Gotham-Book.otf) format("opentype");
}

:root {
  --main-filter-color: #857d74;
}

/* Main */

*,
*:before,
*:after {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

