  }
}

.btn {
  display: inline-block;
  text-decoration: none;
  color: #80705a;
  font-weight: 700;
  text-transform: uppercase;
  font-size: clamp(0.6em, 1vw, 1em);
  padding: 0.5em 1.25em;
  border: 2px solid #80705a;
  border-radius: 2em;
  transition: transform 250ms ease-in-out, opacity 250ms linear;
  white-space: nowrap;
}

.disabled {
