.projet-page-btn::after,
.equipe-page-btn::after {
  content: "";
  display: block;
  width: 0;
  height: 2px;
  background: #525252;
  transition: width 0.3s;
}
.projet-page-btn:hover::after,
.equipe-page-btn:hover::after {
  width: 100%;
  transition: width 0.3s;
}

